export interface UseFormSubmitOptions {
    triggers?: string[];
}
/**
 *
 * @param options
 * @param {string[]} options.trigger A set of paths to trigger validation for
 */
export default function useFormSubmit({ triggers }?: UseFormSubmitOptions): readonly [(...args: any[]) => void, {
    submitCount: number;
    submitAttempts: number;
    submitting: boolean;
    errors: import("./types").Errors;
}];
